import axios from 'axios'
// import { setAlert } from './alert'

import {
  GET_PROFILE,
  PROFILE_ERROR
} from './types'

// get current user profile
export const getCurrentProfile = () => async dispatch => {
  const config = {
    headers: { 'Access-Control-Allow-Origin': '*',
      'Content-Type': 'application/json' }
  }
  try {
    // pulls user id from token
    const res = await axios.get(process.env.PORT || 'http://localhost:5000/api/profile/me', config)
    dispatch({
      Type: GET_PROFILE,
      payload: res.data
    })
  } catch (err) {
    console.log(localStorage)
    console.log('dispatching profile error, but there is a problem: FROM actions/profile.js')
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status }
    })
  }
}
