import React, { Fragment, useState } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { Form, FormControl, FormGroup, FormLabel } from 'react-bootstrap'

const PickStonks = props => {
  const [formData, setFormData] = useState({
    stonks: ''
  })
  // destructure by pulling from form data
  const {
    stonks
  } = formData

  return (
    <Fragment>
      <h1>Here is where you add stonks to your list</h1>
      <Form>
        <FormGroup controlId='stonksChosen'>
          <FormLabel>Stonks in our Database</FormLabel>
          {/* <FormControl as='select'>
            <option> TSLA </option>
            <option> AAL </option>
            <option> GE </option>
            <option>  WFC </option>
            <option> GME </option>
          </FormControl> */}
          {['radio'].map((type) => (
            <div key={`default-${type}`} className='mb-3'>
              <Form.Check
                type={type}
                id={`tsla`}
                label={`TSLA`}
              />
              <Form.Check
                type={type}
                id={`aal`}
                label={`AAL`}
              />

            </div>

          ))}
        </FormGroup>

      </Form>
    </Fragment>
  )
}

PickStonks.propTypes = {

}

export default PickStonks
